<?php

$cars = [
    [
        "brand" => "Ford", 
        "model" => "Mustang",
         "year" => 1764
    ],
    [
        "brand" => "BMW", 
        "model" => "jjgf",
         "year" => 1968
    ],
    [
        "brand" => "Toyota", 
        "model" => "sadfd",
         "year" => 1977
    ],
];

function filterByBrand($cars, $model){
    $filterCars = [];
    foreach($cars as $car){
        if($car['brand'] === $model){
            $filterCars[] = $car;
        }
    }

    return $filterCars;
}

